import React from 'react';
import "./App2.css";


function Box(props){
    return (
        <div className="comment_box">
             <div  id="photo"></div>
             <div id="comment_name">{props.who}</div>
             <div id="comment">{props.comment}</div>
        </div>
    )
}

class App2 extends React.Component{
    constructor(){
      super();
      this.state={
        item:[{who:"teacher",comment:"its holiday today"},]
      }
      this.handler=this.handler.bind(this);
      this.handler1=this.handler1.bind(this);
      this.handlers=this.handlers.bind(this);
      this.who="Unknown";
      this.comment="";
    }

    handlers(event){
       let who=this.who;
       let comment=this.comment;
        console.log("its reaching  here");
        if(comment!==""){
            this.setState((prevstate)=> {
                return {
                    item:[...prevstate.item,{who:who,comment:comment}]
                }
            })
        }
        event.preventDefault();
        console.log(this.state.item);
        this.who="Unknown";
        this.comment="";
    }

    handler(event){
        this.who=event.target.value;
    }
    handler1(event){
        this.comment=event.target.value;
    }

    refresh(){
      window.location.reload();
    }
    render(){
        // console.log(this.state.list);
       return(
           <div id="comment_container">
             <div id="form1">
                <form onSubmit={this.handlers}>
                    <input id="in1" type="text" placeholder="type your name" onChange={this.handler}/>
                    <input id="in2" type="text" placeholder="type your comment" onChange={this.handler1}/>
                    <a href="www.google.com" onClick={this.refresh}>cancel</a>
                    <button id="btn1" type="submit">share </button>
                </form>
              </div>
              <div>

              </div>
              {this.state.item.map((val)=><Box who={val.who} comment={val.comment}/>)}
              {/* <Box who="teacher" comment="taking class"></Box> */}

           </div>

       )
    }

}

export default App2;
