import React from 'react';
import "./App2.css";


function Box(props){
    return (
        <div className="comment_box">
             <div  id="photo"></div>
             <div id="comment_name">{props.who}</div>
             <div id="comment">{props.comment}</div>
        </div>
    )
}

class App2 extends React.Component{
    constructor(){
      super();
      this.state={
        item:[{who:"teacher",comment:"its holiday today"},]
      }
      this.handlers=this.handlers.bind(this);
    }

    handlers=(event)=>{

        if(this.comment.value!==""){
              var new_item={who:this.who.value,comment:this.comment.value}
        }
        this.setState((prevstate)=> {
            return {
                item:[...prevstate.item,{who:this.who.value,comment:this.comment.value}]
            }
        })
        event.preventDefault();
        console.log(this.state.item);
        this.refresh();
    }

    refresh(){
      this.who.value="";
      this.comment.value="";
    }
    render(){
        // console.log(this.state.list);
       return(
           <div id="comment_container">
             <div id="form1">
                <form onSubmit={this.handlers}>
                    <input id="in1" type="text" placeholder="type your name" ref={(who)=> this.who=who}/>
                    <textarea id="in2" type="text" placeholder="type your comment" ref={(comment)=>this.comment=comment}/>
                    <a href="www.google.com" onClick={this.refresh}>cancel</a>
                    <button id="btn1" type="submit">share </button>
                </form>
              </div>
              <div id="comment_line">
                 {this.state.item.map((val)=><Box who={val.who} comment={val.comment}/>)}
              </div>

              {/* <Box who="teacher" comment="taking class"></Box> */}

           </div>

       )
    }

}

export default App2;
