import "./notepad.css";
import react from 'react';

function Box(props){
    return(
        <div className="box">
            {props.name}:{props.note};
        </div>

    )
}


class Notepad extends react.Component{
    constructor(){
        super();
        this.state={
            list:[],
            intext:true,
        }
        // this.list=[];
        // this.state={intext:true};
    }

    handler=(event)=>{

        let title=event.target.name;
        let note=event.props.note;
        console.log(e);
        this.setState({
             list:this.state.list.push({title,note}),
             intext:!(this.state.intext),
        }
        );
        // this.list.push({name:title,note:note});
        // this.state.intext=!(this.state.intext);

    }
    render(){
        // console.log(this.state.intext);
        if(this.state.intext){
           return(
               <div id="notepad_input">
                   {/* <div id="notepad_title">Notepad</div> */}
                   <div id="form">
                    <form  onSubmit={this.handler}>
                        <input  id="text_input" type="text" placeholder="note title" name="name"/>
                        <input  id="text_input1" type="text" placeholder="note" note="note"/>
                        <input id="submit_btn" type="submit" value="submit"/>
                    </form>
                   </div>

               </div>
           )
        }else{
            let all_notes=[];
            let n=this.list.size();
            for(let i=n-1;i>=0;i--){
                all_notes.push(<Box note={this.list[i]}/>);
            }
            return (
                 <div id="notepad_display">


                 </div>


            )


        }
    }

}
export default Notepad;
