import {BrowserRouter} from 'react-router-dom';

class Application extends React.Component{
  render(){
    return(
    <div>
      <App/>
      <App1/>
    </div>
    )
  }
}

class Application1 extends React.Component{
    render(){
        return(
          <div>
            <App/>
            <App2/>
          </div>

        )
     }

}
