import "./noti_box.css";
import react from 'react';

function Box(props){
    return(
        <div className="box">
            title:{props.note};
        </div>

    )
}


class Notepad extends react.Component{
    constructor(){
        super();
        this.list=[];
        this.state={intext:true};
    }

    handler(event){
        this.list.push(event.props.name);
        this.state.intext=!this.state.intext;
    }
    render(){

        if(this.state.intext){
           return(
               <div id="notepad_input">
                   <form onSubmit={this.handler}>
                       <input  id="text_input" type="text" placeholder="note title" name="note"/>
                       <input type="submit" value="submit"/>
                   </form>
               </div>
           )
        }else{
            let all_notes=[];
            let n=this.list.size();
            for(let i=n-1;i>=0;i--){
                all_notes.push(<Box note={noti_all[i]}/>);
            }
            return (
                 <div id="notepad_display">


                 </div>


            )


        }
    }

}
export default Notepad;
